fn main() {
    let s = String::from("");
    let mut v: Vec<i64> = Vec::new();
    let mut max: i64 = 0;
    let mut num: i64 = 1;
    
    for i in 0..s.len() {
        v.push(s[i..i+1].parse().unwrap());

    }

    for i in 0..s.len()-12 {
        for j in i..i+13 {
            num *= v[j];
        }
        if num > max {
            max = num;
        }

        num = 1;
    }

    println!("{}",max);
}

